var Bluebird = require("bluebird")

function punish(config) {
  return new Bluebird(function (resolve, reject) {
    setTimeout(() => {
      resolve([
        { type: "warning", signature: "async" },
        { type: "test", signature: "async" }
      ])
    }, 250)
  })
}

module.exports = {
  punish: punish
}
